<span class="line"><span style="color:#C678DD;">		else</span><span style="color:#C678DD;"> if</span><span style="color:#ABB2BF;"> (</span><span style="color:#E5C07B;">p</span><span style="color:#ABB2BF;">.</span><span style="color:#E06C75;">val</span><span style="color:#56B6C2;"> &lt;</span><span style="color:#E5C07B;"> node</span><span style="color:#ABB2BF;">.</span><span style="color:#E06C75;">val</span><span style="color:#56B6C2;"> &amp;&amp;</span><span style="color:#E5C07B;"> q</span><span style="color:#ABB2BF;">.</span><span style="color:#E06C75;">val</span><span style="color:#56B6C2;"> &lt;</span><span style="color:#E5C07B;"> node</span><span style="color:#ABB2BF;">.</span><span style="color:#E06C75;">val</span><span style="color:#ABB2BF;">) </span><span style="color:#E06C75;">node</span><span style="color:#56B6C2;"> =</span><span style="color:#E5C07B;"> node</span><span style="color:#ABB2BF;">.</span><span style="color:#E06C75;">left</span><span style="color:#ABB2BF;">;</span></span>
