<span class="line"><span style="color:#C678DD;">	let</span><span style="color:#E06C75;"> gap</span><span style="color:#56B6C2;"> =</span><span style="color:#E5C07B;"> Math</span><span style="color:#ABB2BF;">.</span><span style="color:#61AFEF;">floor</span><span style="color:#ABB2BF;">(</span><span style="color:#E06C75;">n</span><span style="color:#56B6C2;"> /</span><span style="color:#D19A66;"> 2</span><span style="color:#ABB2BF;">); </span><span style="color:#7F848E;font-style:italic;">// 初始增量</span></span>
