<span class="line"><span style="color:#61AFEF;">-10^4</span><span style="color:#ABB2BF;"> &lt;</span><span style="color:#98C379;">=</span><span style="color:#98C379;"> nums[i]</span><span style="color:#ABB2BF;"> &lt;</span><span style="color:#98C379;">=</span><span style="color:#98C379;"> 10^4</span></span>
